import React, { Component } from 'react';
//import Permissions from 'react-native-permissions';
import {
  Button,
  Platform,
  StyleSheet,
  Text,
  View
} from 'react-native';
import {
  NativeEventEmitter,
  NativeModules
} from 'react-native';

const ChirpConnect = NativeModules.ChirpConnect;
const ChirpConnectEmitter = new NativeEventEmitter(ChirpConnect);

const key = 'BE9CcAc0F91dD3E5F546f7254';
const secret = '2bCC2406b7A506bd55c4bfCEFA475d1ADa41fBdFcCfB6BaccB';
const config = '';

export default class App extends Component<{}> {

  constructor() {
    super();
    this.state = {'initialised': false, 'status': 'Sleeping',
                   'data': '?', dataSend : 100}
    this.onPress = this.onPress.bind(this);
  }

  

  async componentDidMount() {
    /*const response = await Permissions.check('microphone')
    if (response !== 'authorized') {
      await Permissions.request('microphone')
    }*/

    this.onStateChanged = ChirpConnectEmitter.addListener(
      'onStateChanged',
      (event) => {
        if (event.status === ChirpConnect.CHIRP_CONNECT_STATE_STOPPED) {
          this.setState({ status: 'Stopped' });
        } else if (event.status === ChirpConnect.CHIRP_CONNECT_STATE_PAUSED) {
          this.setState({ status: 'Paused' });
        } else if (event.status === ChirpConnect.CHIRP_CONNECT_STATE_RUNNING) {
          this.setState({ status: 'Running' });
        } else if (event.status === ChirpConnect.CHIRP_CONNECT_STATE_SENDING) {
          this.setState({ status: 'Sending' });
        } else if (event.status === ChirpConnect.CHIRP_CONNECT_STATE_RECEIVING) {
          this.setState({ status: 'Receiving' });
        }
      }
    );

    this.onReceived = ChirpConnectEmitter.addListener(
      'onReceived',
      (event) => {
        if (event.data.length) {
          this.setState({ data: event.data });
          
          // echo
          this.setState({status : 'Received'});
         // this.setState({ dataSend: event.data });
          const echoData = parseInt(String(event.data),10);
          ChirpConnect.send([echoData]);
  
        }
      }
    )

    this.onError = ChirpConnectEmitter.addListener(
      'onError', (event) => { console.warn(event.message) }
    )

    try {
      ChirpConnect.init(key, secret);
      ChirpConnect.setConfig(config);
      ChirpConnect.start();
      this.setState({ initialised: true })
    } catch(e) {
      console.warn(e.message);
    }
  }

  componentWillUnmount() {
    this.onStateChanged.remove();
    this.onReceived.remove();
    this.onError.remove();
  }

  onPress() {
    //ChirpConnect.send([0, 1, 2, 3, 4]);
    let data = this.state.dataSend;
    ChirpConnect.send([data]);
    // increase data
    this.setState({ dataSend : this.state.dataSend + 1});  
  }


  render() {
    return (
      <View style={styles.container}>
        <View style={{flex:0.1}}>
        <Text style={styles.welcome}>
          Welcome to Chirp!
        </Text>
        </View>
        
        <View style={{flex:0.1}}>
        <Text style={styles.instructions}>
          {this.state.status}
        </Text>
        </View>
        
        <View style={{flex:0.6, 
                      borderColor :'orange',
                      borderRadius : 10,
                      borderWidth : 1,
                       justifyContent:'center',
                       margin: 10,

                       }}>
          <Text style={styles.data}>
            {this.state.data}
          </Text>
        </View>
        
        <View style={{flex:0.2, justifyContent:'center',
                      margin:5,
                      height : '100%',}}>
        <Button
          onPress={this.onPress}
          title='SEND'
          disabled={!this.state.initialised}
        />
        </View>
        
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'flex-start',
  //  alignItems: 'center',
    backgroundColor: '#F5FCFF',
  },
  welcome: {
    fontSize: 20,
    textAlign: 'center',
    margin: 10,
  },
  instructions: {
    fontSize : 20,
    padding: 10,
    textAlign: 'center',
    color: '#333333',
    margin: 5,
  },

  data: {
    fontSize : 100,
    textAlign: 'center',
    color: '#333333',
  },
});
